
input = """"""

 # part_1 = 4 (1848)
 # part_2 = 14
marker_length = 4

for i in range(marker_length, len(input)):
  four_markers = input[i-marker_length : i]
  is_found = True
  for ch in four_markers:
    if four_markers.count(ch) > 1:
      is_found = False

  if is_found:
    print(f'FOUND AT INDEX: {i}')
    print(f'four_markers: {four_markers}')
    break
